// DEPENDENCIES

// packages
import React from 'react'

// components
import Header from './Header.js'
import Nav from './Nav.js'
import Main from './Main.js'


// COMPONENT CLASS

class App extends React.Component {

  // STATE

  constructor(props) {
  super(props)
  this.state = {
    view: {
      page: 'home',
      pageTitle: 'Overture Portfilios',
    },
    formInputs: {
      name: null,
      image: null,
      body: null,
      link: null,
      id: null
    }
  }
}


  // HANDLERS

  handleView = (view, postData) => {
     // declare an empty variable
     let pageTitle = ''
     let formInputs = {
       name: '',
       image: '',
       body: '',
       link: '',
       id: null
     }
     // decide the pageTitle based on the view
     switch (view) {
       case 'home':
         pageTitle = 'Portfilio'
         break
       case 'addPost':
         pageTitle = "Let's add a project!"
         break
       case 'editPost':
         pageTitle = 'Wanna make a quick fix?'
         formInputs = {
           name: postData.name,
           image: postData.image,
           body: postData.body,
           link: postData.link,
           id: postData.id
         }
         break
       default:
         break
     }
     // update the state
     this.setState({
       view: {
         page: view,
         pageTitle: pageTitle
       },
       formInputs: formInputs
     })
   }

  // RENDER

  render () {
    return (
      <div className="large-container">
        <Header/>
        <div className="main-container">
        <Nav handleView={this.handleView}/>
        <Main
        view={this.state.view}
        handleView={this.handleView}
        formInputs={this.state.formInputs}
        />
        </div>
      </div>
    )
  }
}


// EXPORT

export default App
